# FAISS 벡터 DB 불러오기
vector_db = FAISS.load_local("faiss_index", embeddings, allow_dangerous_deserialization=True)


# 검색 함수
def search_news(query, top_k=3, similarity_threshold=0.4):
    """입력한 쿼리에 관련된 뉴스 기사를 검색"""
    docs_with_scores = vector_db.similarity_search_with_score(query, k=top_k) # 유사도 포함 검색
    filtered_docs = [(doc, score) for doc, score in docs_with_scores if score >= similarity_threshold]

    if not filtered_docs:
      print("검색된 뉴스가 유사도 기준을 충족하지 않습니다.")

    merged_text = " ".join(doc.page_content for doc, _ in filtered_docs)
    merged_text = merged_text.replace("\n", " ")

    # 유사도 정보 출력
    print("검색된 뉴스 유사도 점수:")

    for idx, (doc, score) in enumerate(filtered_docs):
        print(f"  {idx+1}. {doc.metadata['press']} - {doc.metadata['date']} (유사도: {score:.4f})")

    print("\n 뉴스 본문 통합 결과:")
    print(merged_text)


# 테스트 실행
query = "금리 인하"
search_news(query)